>
{loading ? 
    (
      <Box sx={{display:"flex",justifyContent:"center",maxWidth:"500",mt:"25vh"}}>
        <CircularProgress color="success" />
      </Box>
    ):
    (<>
  <Grid container spacing={5}>
    {contacts.map((data, index) => {
      return (
        <>
          <Grid
            key={index}
            item
            xs={12}
            md={6}
            sm={6}
            lg={4}
          >
          <motion.div
            initial={{y: "-80px", opacity: 0}}
            animate={{y: 0,opacity: 1}}
          >
            <CardCategory
              key={index}
              urlToImage={
                data.image
                  ? data.image
                  : ""
              }
              title={data.title}
              description={
                data.description
                  ? data.description
                  : ""
              }
              
              
              maxWidth={350}
              maxHeight={480}
              minWidth={270}
              minHeight={480}
              minDes
              descH={220}
              mT={2.5}
            />
          </motion.div>
          </Grid>
        </>
      );
    })}
