import { Link } from "react-router-dom";
// import isEmail from "validator/lib/isEmail";
import axios from "axios";
import React, { useState,useEffect } from "react";


const Signin = () => {

  var id = sessionStorage.getItem('id');

  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts);
const mycomp = contacts.filter((obj) => {
  return obj.uid === id;
});
  
  return (
    <div>
    
                <Link to="/" className="nav-link active text-dark">
                  <buton>back</buton>
                </Link>

                {mycomp.length > 0 ? (
        
        mycomp.map((pro) => {
          return (
            
        <div onClick={()=>setId(pro)}>
          <div class="card" >
          <div class="card-header" >
            <img src={pro.image} alt="img" />
          </div>
          <div class="card-body">
          
            <h4>
            {pro.title}
            </h4>
            <p>
            {pro.description.substring(0, 70) + "..."}
            </p>
            
          </div>
          <Link to="/SignIn" className="nav-link active text-dark"> <button onClick={()=>redirect(pro)}>view</button></Link>
          </div>
          </div>
          
          );
        })
      ) : (
        <p>Loading...</p>
      )} 
              
      </div>
  );
};

export default Signin;
