import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import "./home.css";
import axios from "axios";




function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts)

const [id, setId] = useState("");
const [open, setOpen] = useState(false);
const redirect = () =>{
  dialog.show(); 
}

(function() {    
  var dialog = document.getElementById('myFirstDialog');    
  document.getElementById('show').onclick = function() {    
      dialog.show();    
  };    
  document.getElementById('hide').onclick = function() {    
      dialog.close();    
  };    
})();   


  return (
    <>
    

<div class="container-2">
       
<div>  
<div id="myFirstDialog" style="width:50%;background-color:#F4FFEF;border:1px dotted black;">  
<p><q>I am so clever that sometimes I don't understand a single word of what I am saying.   
</q> - <cite>Oscar Wilde</cite></p>  
<button id="hide">Close</button>  
</div>  
<button id="show">Show Dialog</button>  
</div>  
    

        <div class="flex-box-container-2" >
        {contacts.length > 0 ? (
        
        contacts.map((pro) => {
          return (
            
        <div onClick={()=>setId(pro)}>
          <div class="card" onClick={redirect()}>
          <div class="card-header" >
            <img src={pro.image} alt="img" />
          </div>
          <div class="card-body">
          
            <h4>
            {pro.title}
            </h4>
            <p>
            {pro.description.substring(0, 70) + "..."}
            </p>
            
          </div>
          <Link to="/SignIn" className="nav-link active text-dark"> <button>view</button></Link>
          </div>
          </div>
          
          );
        })
      ) : (
        <p>Loading...</p>
      )} 
        </div>
    </div>


  
    
     <Link to="/SignUp" id="mybutton">
     
      <i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
      
      </Link>
    </>
   
  );
}
export default Home;
