import React, { useState, useEffect } from "react";
import axios from "axios";
import localid from "../../helper/localid";
import Footer from "../footer";

const Search = () => {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/user", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };

  const id = localid();

  useEffect(() => {
    getAllContacts();
  }, []);

  const mycomp = contacts.filter((obj) => {
    return obj._id === localid();
  });

  console.log(mycomp);

  function getDate(isoDate) {
    var date = isoDate.substring(0, 10);

    return date;
  }

  return (
    <div class="vh-100">
      <div calss="vh-100">
        <div className="colu">
          {mycomp.length > 0 ? (
            mycomp.map((pro) => {
              return (
                <section class="vh-100">
                  <div class="container py-5 h-100">
                    <div class="row d-flex justify-content-center align-items-center h-100">
                      <div class="col col-lg-6 mb-4 mb-lg-0 vh-100">
                        <div class="card mb-3">
                          <div class="row g-0">
                            <div class="col-md-4 gradient-custom text-center text-white">
                              <img
                                src="https://www.seekpng.com/png/detail/202-2024774_my-profile-comments-my-profile-icon-png.png"
                                alt="Avatar"
                                class="img-fluid my-5"
                              />
                              <h4>Name: </h4>
                              <p>Web Designer</p>
                              <i class="far fa-edit mb-5"></i>
                            </div>
                            <div class="col-md-8">
                              <div class="card-body p-4">
                                <h6>Information</h6>
                                <hr class="mt-0 mb-4" />
                                <div class="row pt-1">
                                  <div class="col-6 mb-3">
                                    <h6>Email</h6>
                                    <p class="text-muted">info@example.com</p>
                                  </div>
                                  <div class="col-6 mb-3">
                                    <h6>Phone</h6>
                                    <p class="text-muted">123 456 789</p>
                                  </div>
                                </div>
                                <h6>Projects</h6>
                                <hr class="mt-0 mb-4" />
                                <div class="row pt-1">
                                  <div class="col-6 mb-3">
                                    <h6>Recent</h6>
                                    <p class="text-muted">Lorem ipsum</p>
                                  </div>
                                  <div class="col-6 mb-3">
                                    <h6>Most Viewed</h6>
                                    <p class="text-muted">Dolor sit amet</p>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </section>
              );
            })
          ) : (
            <div class="d-flex align-items-center justify-content-center vh-90">
              <div class="text-center">
                <h1 class="display-1 fw-bold text-primary">Opps!</h1>
                <p class="fs-3">You are not Logged In.</p>
                <p class="lead"></p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Search;
