import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";
import { Button, Grid, Card } from "@material-ui/core";
import "./home.css";
import axios from "axios";
import Col from 'react-bootstrap/Col';

function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts)

const [id, setId] = useState("");

  return (
    <>
       {contacts.length > 0 ? (
        
          contacts.map((pro) => {
            return (
              <Col xs={12} md={6} lg={4} key={data.id}>
    <Card style={{ width: '18rem' }}>
      <Card.Header></Card.Header>
      <Card.Img variant="top" src={data.image} />
      <Card.Body>
        <Card.Title>{pro.title}</Card.Title>
        <Card.Text>
          {pro.description}
        </Card.Text>
       
      </Card.Body>
    </Card>
  </Col>
              

              
            );
          })
        ) : (
          <p>No Compalint posted yet</p>
        )}
     
    
     <Link to="/SignUp" id="mybutton">
     
      <i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
      
      </Link>
    </>
   
  );
}
export default Home;
