import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { setAuthentication, isAuthenticated } from "../helper/auth";
import { showErrorMsg } from "../helper/message";
import { showLoading } from "../helper/loading";

// import isEmail from "validator/lib/isEmail";

import isEmpty from "validator/lib/isEmpty";
import { signin } from "../api/auth";
import "./signin.css";

const Signin = () => {
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated() && isAuthenticated().role === 1) {
      navigate("/admin/dashboard");
    } else if (isAuthenticated() && isAuthenticated().role === 2) {
      navigate("/department/dashboard");
    } else if (isAuthenticated() && isAuthenticated().role === 0) {
      navigate("/products");
    }
  }, [navigate]);

  const [formData, setFormData] = useState({
    email: "",
    password: "",
    errormsg: false,
    loading: false,
  });

  const { email, password, errormsg, loading } = formData;

  const handleChange = (evt) => {
    setFormData({
      ...formData,
      [evt.target.name]: evt.target.value,
      errormsg: "",
    });
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();

    if (isEmpty(email) || isEmpty(password)) {
      setFormData({
        ...formData,
        errormsg: "All fields are required !",
      });
    } else {
      const { email, password } = formData;
      const data = { email, password };

      setFormData({
        ...formData,
        loading: true,
      });

      signin(data)
        .then((response) => {
          setAuthentication(response.data.token, response.data.user);

          if (isAuthenticated() && isAuthenticated().role === 1) {
            console.log("Redirecting to admin dashboard");
            navigate("/admin/dashboard/Pending");
          } else if (isAuthenticated() && isAuthenticated().role === 2) {
            console.log("Redirecting to Department dashboard");
            navigate("/waterdepartment/dashboard/Pending");
          } else if (isAuthenticated() && isAuthenticated().role === 3) {
            console.log("Redirecting to Department dashboard");
            navigate("/powerdepartment/dashboard/Pending");
          } else if (isAuthenticated() && isAuthenticated().role === 4) {
            console.log("Redirecting to Department dashboard");
            navigate("/sewagedepartment/dashboard/Pending");
          } else {
            console.log("Redirecting to user dashboard");
            navigate("/products/add");
          }
        })
        .catch((err) => {
          console.log("signin api function error", err);
          setFormData({
            ...formData,
            loading: false,
            errormsg: err.response.data.errorMessage,
          });
        });
    }
  };

  const ShowSignInForm = () => (
<div class="row">
  <div class="column1" >
      <Fragment>
              
        <Link to="/" className="nav-link active text-primary">
         <i className="fas fa-home"></i> <b>Home</b>
        </Link>       
      </Fragment>
  </div>
  <div class="column2" >
  <form onSubmit={handleSubmit} >
           <p><label>Write A Blog</label></p>
           <div class="form-group">
    <label>Title</label>
    <input type="text" class="form-control" placeholder="Blog Title" name="title" value={title} onChange={handleChange}/>
  </div>
           <br></br>
           <div class="form-group">
    <label >Description</label>
    <textarea class="form-control"  rows="7" placeholder="Blog Description" name="description" value={description} onChange={handleChange}></textarea>
  </div>
         <br></br>
         <input type="submit" value="Submit"/>
    </form>
  </div>
 
</div>
  );

  return (
    <div>
      <div className="container3">
        <div className="container4">
          {loading && <div className="text-center pb=4">{showLoading()}</div>}
          {errormsg && showErrorMsg(errormsg)}
          {ShowSignInForm()}
        </div>
      </div>
      
    </div>
  );
};

export default Signin;
