import "./PostComplaint.css";
import React, { useState } from "react";

const countriesData = [
  {
    name: "Gangtok",
    states: [
      "Gangtok(Nandok)",
      "Khamdong",
      "Martam",
      "Parakha",
      "Rakdong_Tintek",
      "Ranka",
      "Reghu",
      "Rhenock",
    ],
  },
  {
    name: "Gyalshing",
    states: [
      "Chongrang",
      "Chumdung",
      "Daramdin",
      "Dentam",
      " Gyalshing",
      "Hee_Martam",
      "Kaluk",
      "Mangalbarey",
      "Soreng",
      " Yuksom",
    ],
  },
  {
    name: "Mangan",
    states: ["Chungthang", " Kabi", " Mangan", "Passingdang"],
  },
  {
    name: "Namchi",
    states: [
      "Jorthang",
      " Namchi",
      " Namthang",
      "Ravangla",
      " Sikkip",
      "Sumbuk",
      " Timi_Tarku",
      "Yangang",
    ],
  },
  {
    name: "Pakyong",
    states: ["Duga", "Pakyong"],
  },
  {
    name: "Soreng",
    states: ["Soreng"],
  },
];

function Form() {
  const [{ country, state }, setData] = useState({
    country: "Gangtok",
    state: "",
  });

  const countries = countriesData.map((country) => (
    <option key={country.name} value={country.name}>
      {country.name}
    </option>
  ));

  const states = countriesData
    .find((item) => item.name === country)
    ?.states.map((state) => (
      <option key={state} value={state}>
        {state}
      </option>
    ));

  function handleCountryChange(event) {
    setData((data) => ({ state: "", country: event.target.value }));
  }

  function handleStateChange(event) {
    setData((data) => ({ ...data, state: event.target.value }));
  }

  return (
    <div class="Postmain">
      <div class="title">Registration</div>
      <div class="content">
        <Form>
          <div>
            District
            <select value={country} onChange={handleCountryChange}>
              {countries}
            </select>
          </div>
          <div>
            Block
            <select value={state} onChange={handleStateChange}>
              {states}
            </select>
          </div>
        </Form>
      </div>
    </div>
  );
}

export default Form;
