import { useParams } from "react-router-dom";
import axios from "axios";
import "./PostComplaint.css";
import React, { useState, useEffect} from "react";


function ProductDisplay() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
		const config = {
			headers: {
				"Content-Type": "application/json",
			},
		};
		try {
			const res = await axios.get("http://localhost:5000/postcomp", config);
			setContacts(res.data);
		} catch (err) {
			console.error("error", err);
		}
	};

	useEffect(() => {
		getAllContacts();
	}, []);

  const { _id } = useParams();
  return (
    <div className="listOfProducts">
      <div className="productDisplay">
        <nav class="navbar navbar-expand-lg navbar-light bg-warning rounded ">
          {contacts[_id ].uid}{" "}
        </nav>
        <p>{contacts[_id ].firstName}</p>
        <p>{contacts[_id ].lastName}</p>{" "}
        <p>{contacts[_id ].description}</p>{" "}
        <p>{contacts[_id ].phoneNo}</p>{" "}
       
      </div>
    </div>
  );
}

export default ProductDisplay;
