import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";

import "./home.css";
import axios from "axios";
import { Dialog, DialogContent, DialogContentText } from "@material-ui/core";
import { Button } from "bootstrap";



function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts)

const [id, setId] = useState("");
const [open, setOpen] = useState(false);
const redirect = () =>{
  setOpen(true);
}

const handleClose =  () => {
  setOpen(false);
}


  return (
    <>
    

<div class="container-2">
       

<Dialog
        fullScreen
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogContent>
          <DialogContentText component={"div"} style={{ textAlign: "center" }}>
            <form className="contact-form ">
              <nav class="navbar card-header  ">
                <Button
                  class="btn-close ms-auto "
                  aria-label="Close"
                  onClick={() => handleClose(id)}
                ></Button>
              </nav>
              <div className="card">
                <h4>
                  <b>Complianer Details</b>
                </h4>
                <p class="m-0">
                  </p>
                  </div>
            </form>
          </DialogContentText>
        </DialogContent>
      </Dialog>
        

        <div class="flex-box-container-2" >
        {contacts.length > 0 ? (
        
        contacts.map((pro) => {
          return (
            
        <div onClick={()=>setId(pro)}>
          <div class="card" onClick={redirect()}>
          <div class="card-header" >
            <img src={pro.image} alt="img" />
          </div>
          <div class="card-body">
          
            <h4>
            {pro.title}
            </h4>
            <p>
            {pro.description.substring(0, 70) + "..."}
            </p>
            
          </div>
         
          </div>
          </div>
          );
        })
      ) : (
        <p>Loading...</p>
      )} 
        </div>
    </div>


  
    
     <Link to="/SignUp" id="mybutton">
     
      <i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
      
      </Link>
    </>
   
  );
}
export default Home;
