import React, { useState, useEffect } from "react";
// import { ProductsData } from "../../ProductsData";
import { useNavigate, Link } from "react-router-dom";
import "../App.css";
import axios from "axios";
import loaclemail from "../../helper/localemail";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";





const Pending3 = () => {
  // const navigate = useNavigate();
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postcomp", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };

  useEffect(() => {
    getAllContacts();
  }, []);
  
  const allcomp = contacts.filter((obj) => {
    return obj.adminstatus === 1;
  });
  const mydepts = allcomp.filter((obj) => {
    return obj.department === "Public Health and Engineering Department - Sewage";
  });
  const mycomp = mydepts.filter((obj) => {
    return obj.deptstatus === 0;
  });
  mycomp.sort(function (a, b) {
    return a.uid - b.uid;
  });

  // sort by name
  mycomp.sort(function (a, b) {
    const nameA = a.uid; // ignore upper and lowercase
    const nameB = b.uid; // ignore upper and lowercase
    if (nameA > nameB) {
      return -1;
    }
    if (nameA < nameB) {
      return 1;
    }

    // names must be equal
    return 0;
  });
  // console.log(mycomp);

  // const [currentContact, setCurrentContact] = useState({});
	const [id, setId] = useState("");

	// const getContactById = async (id) => {
	// 	const config = {
	// 		headers: {
	// 			"Content-Type": "application/json",
	// 		},
	// 	};
	// 	try {
	// 		const res = await axios.get(`http://localhost:5000/postcomp/${id}`, config);
	// 		setCurrentContact(res.data);
	// 	} catch (err) {
	// 		console.error("error", err);
	// 	}
	// };

	// useEffect(() => {
	// 	getContactById(id);
	// }, [id]);
  const [value, setvalue] = useState("All");
  const [open, setOpen] = useState(false);
  const [open1, setOpen1] = useState(false);
  function notupdate(){
    setOpen1(false);
    setId((id) => ({ ...id, deptstatus:0 }));
  }
  const update= async()=>{
    const config = {
			headers: {
				"Content-Type": "application/json",
			},
		};
    
		try {
      console.log(id);
			await axios.put(
				`http://localhost:5000/postcomp/${id.uid}`,
				id,
				config
			);
      
		
		} 
    catch (err) {
			console.error("error", err);
		}
    setOpen1(false);  
  }


  const handleClose= async(pro)=> {
    setOpen(false);
    const config = {
			headers: {
				"Content-Type": "application/json",
			},
		};
    
		try {
      console.log(id);
			await axios.put(
				`http://localhost:5000/postcomp/${pro.uid}`,
				id,
				config
			);
      
      setId({id});
		
		} 
    catch (err) {
			console.error("error", err);
		}
    
  }

  const handleEdit = async (pro) =>{
		setOpen(true);
    setId((id) => ({ ...id, deptread:1 }));
    

	};
  const confirm =async(value)=>{
  setOpen1(true);
  console.log(id)
  setId((id) => ({ ...id, deptstatus:value }));
  console.log(id.adminstatus);
  // window.location.reload();
  const form = await axios.post("/api/form", {
    empname,
    email,
    subject
  });


  }

  const handleInputChange = (event) => {
		setId({
			...id,
			[event.target.name]: event.target.value,
		});
	};
	

  function selected() {
    var subjectIdNode = document.getElementById('district');
    
    setvalue(subjectIdNode.options[subjectIdNode.selectedIndex].text);
    console.log("The selected value=" + value);
    
  }
  const mycomp1 = mycomp.filter((obj) => {
    if(value==="All")
    {
      return obj;
    }
    else{
    return obj.district === value;
    }
  });

  function getDate(isoDate){
    var date = isoDate.substring(0, 10);
  
return date;
  }
  
  return (
    <div class="row justify-content-evenly mt-2">
      <Dialog
        fullScreen
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        
        <Dialog
      open={open1}
      
      aria-labelledby="form-dialog-title">
        <DialogContent>
          <DialogContentText component={"div"} style={{ textAlign: "center" }}>
          <form onSubmit={()=>update()}>
            <h5><b>Assign Employee</b></h5>
        <div className="user-card">
          <div className="input-box">
            <span className="details">Employee Name</span>
            <input
              type="text"
              name="empname"
              value={id.empname}
              placeholder="Enter your first name"
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="input-box">
            <span className="details">Employee ID</span>
            <input
              type="text"
              name="empID"
              value={id.empID}
              placeholder="Enter employee ID"
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="input-box">
            <span className="details">Employee Phone Number</span>
            <input
              type="text"
              name="empNo"
              value={id.empNo}
              placeholder="Enter number"
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="input-box">
            <span className="details">Employee Email</span>
            <input
              type="text"
              name="empemail"
              value={id.empemail}
              placeholder="Enter email"
              onChange={handleInputChange}
              required
            />
          </div>
          </div>
          <br></br>
          <p>Do you want to confirm ?</p>
                  <Button type="submit" class="btn btn-success m-1"
                >Confirm</Button>
                <Button onClick={notupdate} class="btn btn-primary m-1"> Close</Button>
          </form>
                 

          </DialogContentText>
          </DialogContent>
          </Dialog>



        <DialogContent>
          <DialogContentText component={"div"} style={{ textAlign: "center" }}>
            <form className="contact-form ">
              <nav class="navbar card-header  ">
                <Button
                  class="btn-close ms-auto "
                  aria-label="Close"
                  onClick={() => handleClose(id)}
                ></Button>
              </nav>
              <div className="card">
                <h4>
                  <b>Complianer Details</b>
                </h4>
                <p class="m-0">
                  <i class="fa-solid fa-user"></i>{" "}
                  <h>
                    {" "}
                    {id.firstName} {id.lastName}
                  </h>
                </p>
                <p class="m-0">
                  <i class="fa-solid fa-location-dot"></i> {id.address}
                </p>
                <p class="m-0">
                  <b>District: </b> {id.district}
                </p>
                <p class="m-0">
                  <b>Block: </b> {id.block}
                </p>
                <p class="m-0">
                  <b>Pincode: </b> {id.pincode}
                </p>
                <container>
                  <Button class="btn btn-primary  btn-sm" disabled>
                    <i class="fa-solid fa-envelope"></i> {id.email}
                  </Button>
                  <Button class="btn btn-primary  btn-sm m-1" disabled>
                    {" "}
                    <i class="fa-solid fa-phone"></i> {id.phoneNo}
                  </Button>
                </container>
              </div>

              <br></br>
              <div className="card">
                <h4>
                  <b>Complaint Details</b>
                </h4>
                <p class="m-0">
                  <Button class="btn btn-primary  btn-sm m-1" disabled>
                    {" "}
                    Complaint ID: {id.uid}
                  </Button>
                  <p>
                    <b>Department: </b> {id.department}
                  </p>
                  <p class="m-0">
                    <i class="fa-solid fa-comment-dots"></i>
                    <b> Description :</b> {id.description}
                  </p>
                </p>
              </div>
              <br></br>
              <div className="card">
                <h4>
                  <b>Assigned Employee</b>
                </h4>
                <p class="m-0">
                  <Button class="btn btn-primary  btn-sm m-1" disabled>
                    {" "}
                    Complaint ID: {id.uid}
                  </Button>
                  <p>
                    <b>Department: </b> {id.department}
                  </p>
                  <p class="m-0">
                    <i class="fa-solid fa-comment-dots"></i>
                    <b> Description :</b> {id.description}
                  </p>
                </p>
              </div>
              <div>
              </div>
              <Button class="btn btn-success m-2" onClick={() => confirm(1)}>Forward</Button>

            </form>
          </DialogContentText>
        </DialogContent>
      </Dialog>
      <div className="colu">
      <div className="input-box">
            <span className="details">District </span>
            <select
              name="district"
              id="district"
              onChange={selected}
            
            >
              <option value="All ">All</option>
              <option value="Gangtok">Gangtok</option>
              <option value="Gyalshing">
              Gyalshing
              </option>
              <option value="Mangan">
              Mangan
              </option>
              <option value="Namchi">
              Namchi
              </option>
              <option value="Pakyong">
              Pakyong
              </option>
              <option value="Soreng">
              Soreng
              </option>
            </select>
          </div>
        {mycomp1.length > 0 ? (
          mycomp1.map((pro) => {
            return (
              <div onClick={() => handleEdit(pro)}>
                <div class="card m-3 p-0" onClick={() => setId(pro)}>
                  <nav className="navbar card-header ">
                  
                    <h5>{pro.department}</h5>
                    <button className="btn btn-primary my-2 my-sm-0 ms-auto p-1 disabled">
                      Complaint ID: {pro.uid} 
                    </button>
                    {
                      pro.deptread === 0 ?(<button className="btn btn-warning   ms-auto p-1 m-0 disabled">
                      <i class="fa-solid fa-bell fa-shake"></i>
                        </button>) : <button className="btn    ms-auto p-1 m-0 disabled">
                  <i class="fa-solid fa-bell "></i>
                  
                    </button>
                    }
                    
                  </nav> 

                  <div class="card-body">
                    <h5 class="card-title">
                    <i class="fa-solid fa-file-signature"></i> {pro.firstName} {pro.lastName}
                    </h5>

                   <p> <i class="fa-solid fa-location-dot"></i> {pro.address}</p>
                    <p><i class="fa-solid fa-calendar"></i> { getDate(pro.createdAt)}</p>
                    <p><i class="fa-solid fa-phone"></i> {pro.phoneNo}</p>

                    <button class="btn btn-primary" >view</button>
                  </div>
                </div>
              </div>
            );
          })
        ) : (
          <p>No Compalint posted yet</p>
        )}
      </div>
    </div>
  );
        }

export default Pending3;
