import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";
import {style} from '@material-ui/core'
import "./home.css";
import axios from "axios";
import { Card, Button, CardGroup, Row, Col } from 'react-bootstrap';


function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts)

const [id, setId] = useState("");

  return (
    <>
       {/* {contacts.length > 0 ? (
        
          contacts.map((pro) => {
            return (
              
                <div class="container">
      <div class="card">
        <div class="card__header">
          <img src={pro.image} alt="card__image" class="card__image" width="100%"/>
        </div>
        <div class="card__body">
          <span class="tag tag-blue">{pro.title}</span>
         
          <p>{pro.description}</p>
        </div>
      </div>
    </div>
              

              
            );
          })
        ) : (
          <p>No Compalint posted yet</p>
        )} */}

<CardGroup style={{display: 'flex', flexDirection: 'row'}}>
            {contacts.length > 0 &&
                contacts.map(d => (
                    <Row xs={1} md={2} className="g-4">
                    {Array.from({ length: 4 }).map((_, idx) => (
                    <Col>
     
                    <Card key={d.uid} style={{ width: '100em' }} style={{flex: 1}}>
                        <Card.Img variant="top" src={d.attributes.image} />
                        <Card.Body>
                            <Card.Title>{d.attributes.title}</Card.Title>
                            <Card.Text>Possibly some text here</Card.Text>
        
                        </Card.Body>
                    </Card>
                    </Col>
                ))}
                </Row>
                ))}
        </CardGroup>


  
    
     <Link to="/SignUp" id="mybutton">
     
      <i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
      
      </Link>
    </>
   
  );
}
export default Home;
