import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";
import { Button } from "@material-ui/core";
import "./home.css";
import axios from "axios";


function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 

  contacts.sort(function (a, b) {
    const nameA = a.uid; // ignore upper and lowercase
    const nameB = b.uid; // ignore upper and lowercase
    if (nameA > nameB) {
      return -1;
    }
    if (nameA < nameB) {
      return 1;
    }

    // names must be equal
    return 0;
  });
console.log(contacts)
  return (
    <>
     
     
     <Button id="mybutton">
     <Link to="/SignUp">
<i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
</Link>
</Button>

  
    </>
   
  );
}
export default Home;
