import React, { useState, useEffect} from "react";
import { ProductsData } from "../../ProductsData";
import { useNavigate } from "react-router-dom";
import "../App.css";

const ListProducts =()=>{
  const navigate = useNavigate();
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
		const config = {
			headers: {
				"Content-Type": "application/json",
			},
		};
		try {
			const res = await axios.get("/postcomp", config);
			setContacts(res.data);
		} catch (err) {
			console.error("error", err);
		}
	};

	useEffect(() => {
		getAllContacts();
	}, []);
  return (
    <div className="listOfProducts">
      <div className="productsList">
        {contacts.map((pro) => {
          return (
            <div
              className="productDisplay"
              onClick={() => {
                navigate(`/products/${pro.id}`);
              }}
            >
              <nav class="navbar navbar-expand-lg navbar-light bg-warning rounded">
                {pro.uid}
                <button
                  class="btn btn-outline-secondary my-2 my-sm-0 ms-auto"
                  type="submit"
                >
                  view
                </button>
              </nav>
              <p>{pro.name}</p>
              <p>{pro.description}</p>
              <p>{pro.Complaint_details}</p>
              <p>{pro.Phone_No}</p>
              
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default ListProducts;
