import React from "react";
import { useParams } from "react-router-dom";
import { conatcts } from "./ListProducts";
import "./PostComplaint.css";

function ProductDisplay() {
  const { id } = useParams();
  return (
    <div className="listOfProducts">
      <div className="productDisplay">
        <nav class="navbar navbar-expand-lg navbar-light bg-warning rounded ">
          {conatcts[id - 1].uid}{" "}
        </nav>
        <p>{conatcts[id - 1].firstName}</p>
        <p>{conatcts[id - 1].lastName}</p>{" "}
        <p>{conatcts[id - 1].description}</p>{" "}
        <p>{conatcts[id - 1].phoneNo}</p>{" "}
       
      </div>
    </div>
  );
}

export default ProductDisplay;
