import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";
import { Button } from "@material-ui/core";
import "./home.css";
import axios from "axios";
import { Card, Row, Col, Container } from "react-bootstrap";


function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts)

const [id, setId] = useState("");

  return (
    <>
    <Container>
      <Row>
       {contacts.length > 0 ? (
        
          contacts.map((pro) => {
            return (
              <Col  xs={12} md={4} lg={3}>
                <div class="container">
      
       
          <img src={pro.image} alt="card__image" class="card__image" width="100%"/>
        </div>
        <div class="card__body">
          <span class="tag tag-blue">{pro.title}</span>
         
          <p>{pro.description}</p>
        </div>
      
   
              
         </Col>     

              
            );
          })
        ) : (
          <p>No Compalint posted yet</p>
        )}
        </Row>
        </Container>
  
    
     <Link to="/SignUp" id="mybutton">
     
      <i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
      
      </Link>
    </>
   
  );
}
export default Home;
