import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";

import "./home.css";
import axios from "axios";



function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts)

const [id, setId] = useState("");

const redirect = (pro) =>{
  
}




  return (
    <>
    

<div class="container-2">
       
        

        <div class="flex-box-container-2" >
        {contacts.length > 0 ? (
        
        contacts.map((pro) => {
          return (
            
        
          <div class="card" onClick={()=>setId(pro)}>
          <div class="card-header" onClick={redirect(pro)}>
            <img src={pro.image} alt="rover" />
          </div>
          <div class="card-body">
          
            <h4>
            {pro.title}
            </h4>
            <p>
            {pro.description.substring(0, 70) + "..."}
            </p>
            
          </div>
          <Button>view</Button>
          </div>
          );
        })
      ) : (
        <p>Loading...</p>
      )} 
        </div>
    </div>


  
    
     <Link to="/SignUp" id="mybutton">
     
      <i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
      
      </Link>
    </>
   
  );
}
export default Home;
