        rejectUnauthorized:false
      }
  }
 

  const defaults = {
    from: "sikkimcms@gmail.com",
  }

  const WelcomeEmail = ({ firstName }) => ({
    subject: `ðŸ‘‹ ${firstName}`,
    body: (
      <div>
        <p>Hello {firstName}!</p>
        <p>Hope you'll enjoy the package!</p>
      </div>
    )
  })

export const mailer = () =>Mailer(
  { transport, defaults },
