import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { PostComp } from "../api/auth";
import { isAuthenticated } from "../helper/auth";
import "./PostComplaint.css";
import { showErrorMsg } from "../helper/message";
import { showLoading } from "../helper/loading";
import isEmpty from "validator/lib/isEmpty";

const PostComplaint = () => {
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated() && isAuthenticated().role === 0) {
      navigate("/user/dashboard");
    }
  }, [navigate]);

  const [formData, setFormData] = useState({
    FirstName: "",
    LastName: "",
    email: "",
    PhoneNo: "",
    errormsg: false,
    loading: false,
  });

  const { FirstName, LastName, email, PhoneNo } = formData;

  const handleChange = (evt) => {
    setFormData({
      ...formData,
      [evt.target.name]: evt.target.value,
      errormsg: "",
    });
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();

    if (
      isEmpty(FirstName) ||
      isEmpty(LastName) ||
      isEmpty(email) ||
      isEmpty(PhoneNo)
    ) {
      setFormData({
        ...formData,
        errormsg: "All fields are required !",
      });
    } else {
      const { FirstName, LastName, email, PhoneNo } = formData;
      const data = { FirstName, LastName, email, PhoneNo };

      setFormData({
        ...formData,
        loading: true,
      });
    }
  };

  const PostComplaintForm = () => (
    <div className="post">
      <div className="container">
        <div className="row">
          <div className="col">
            <h1> Post Complaint</h1>

            <form
              className="alerts"
              id="PostComp"
              onSubmit={handleSubmit}
              noValidate
            >
              <div>
                <label>
                  <span className="lead">First Name</span>

                  <input
                    name="FirstName"
                    value={FirstName}
                    type="text"
                    className="form__input"
                    placeholder="First Name"
                    onChange={handleChange}
                  />
                </label>
              </div>
              <div>
                <label>
                  <span className="lead">Last Name</span>
                  {/* <input placeholder="Enter Your Last Name" type="text" /> */}
                  <input
                    name="LastName"
                    value={LastName}
                    type="text"
                    className="form__input"
                    placeholder="LastName"
                    onChange={handleChange}
                  />
                </label>
              </div>
              <div>
                <label>
                  <span className="lead">Email </span>
                  <input
                    name="email"
                    value={email}
                    type="text"
                    className="form__input"
                    placeholder="Email"
                    onChange={handleChange}
                  />
                </label>
              </div>
              <div>
                <label>
                  <span className="lead">Phone Number </span>
                  <input
                    name="PhoneNo"
                    value={PhoneNo}
                    type="text"
                    className="form__input"
                    placeholder="PhoneNo"
                    onChange={handleChange}
                  />
                </label>
              </div>

              <div>
                <button
                  ClassName="submit"
                  type="submit"
                  class="btn btn-default"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div>
      <div className="post">
        <div className="container">
          {loading && <div className="text-center pb=4">{showLoading()}</div>}
          {errormsg && showErrorMsg(errormsg)}
          {ShowSignInForm()}
        </div>
      </div>
    </div>
  );
};

export default PostComplaint;
