import React ,{useState, useEffect}from "react";
import { Link } from "react-router-dom";
import { Button } from "@material-ui/core";
import "./home.css";
import axios from "axios";


function Home() {
  const [contacts, setContacts] = useState([]);
  const getAllContacts = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get("http://localhost:5000/postblog", config);
      setContacts(res.data);
    } catch (err) {
      console.error("error", err);
    }
  };
  useEffect(() => {
    getAllContacts();
  }, []);
 
console.log(contacts)

const [id, setId] = useState("");

  return (
    <>
       {contacts.length > 0 ? (
          contacts.map((pro) => {
            return (
              <div >
                <div class="card m-2 p-0" onClick={() => setId(pro)}>
                  <nav className="navbar card-header ">
                  <img className="activator" style={{ width: '20%', height: 50 }} src={pro.image} />
                
                  </nav>

                  <div class="card-body">
                    <h5 class="card-title">
                      {pro.title} 
                    </h5>

                    <p>{pro.description}</p>
                   

              
                  </div>
                </div>
              </div>
            );
          })
        ) : (
          <p>No Compalint posted yet</p>
        )}
     
     <Button id="mybutton">
     <Link to="/SignUp">
<i className="fa-solid fa-circle-plus fa-beat  fa-3x" title="Create Blog " id="addpost"></i>
</Link>
</Button>

  
    </>
   
  );
}
export default Home;
